export declare const checkCastReactionsByFarcasterUserQuery = "\n  query CheckCastReactionsByFarcasterUser(\n    $castHashes: [String!]\n    $criteria: FarcasterReactionCriteria!\n    $identity: Identity!\n  ) {\n    FarcasterReactions(\n      input: {\n        filter: {\n          criteria: $criteria\n          castHash: { _in: $castHashes }\n          reactedBy: { _eq: $identity }\n        }\n        blockchain: ALL\n      }\n    ) {\n      Reaction {\n        cast {\n          castedAtTimestamp\n          hash\n        }\n      }\n    }\n  }\n";
