export declare const farcasterChannelDetailsQuery = "\n  query FarcasterChannelDetails($channel: String!) {\n    FarcasterChannels(\n      input: { blockchain: ALL, filter: { channelId: { _eq: $channel } } }\n    ) {\n      FarcasterChannel {\n        name\n        description\n        imageUrl\n        createdAtTimestamp\n        hosts: leadProfiles {\n          profileName\n          fnames\n          fid: userId\n          profileImage: profileImageContentValue {\n            image {\n              extraSmall\n              small\n              medium\n              large\n              original\n            }\n          }\n          custodyAddress: userAddress\n          connectedAddresses {\n            address\n            blockchain\n            chainId\n            timestamp\n          }\n          userAssociatedAddresses\n          followerCount\n          followingCount\n        }\n      }\n    }\n  }\n";
