export declare const farcasterChannelParticipantsQuery = "\n  query FarcasterChannelParticipants(\n    $actionType: [FarcasterChannelActionType!] = [cast, reply]\n    $before: Time\n    $after: Time = \"1970-01-01T00:00:00Z\"\n    $channel: String\n    $limit: Int = 200\n  ) {\n    FarcasterChannelParticipants(\n      input: {\n        filter: {\n          lastActionTimestamp: { _gte: $after, _lte: $before }\n          channelActions: { _in: $actionType }\n          channelId: { _eq: $channel }\n        }\n        blockchain: ALL\n        limit: $limit\n      }\n    ) {\n      FarcasterChannelParticipant {\n        participant {\n          profileName\n          fnames\n          fid: userId\n          profileImage: profileImageContentValue {\n            image {\n              extraSmall\n              small\n              medium\n              large\n              original\n            }\n          }\n          custodyAddress: userAddress\n          connectedAddresses {\n            address\n            blockchain\n            chainId\n            timestamp\n          }\n          userAssociatedAddresses\n          followerCount\n          followingCount\n        }\n      }\n    }\n  }\n";
