export declare const farcasterChannelsByHostQuery = "\n  query FarcasterChannelsByHost(\n    $host: String!\n    $before: Time\n    $after: Time = \"1970-01-01T00:00:00Z\"\n    $limit: Int = 200\n  ) {\n    FarcasterChannels(\n      input: {\n        filter: {\n          createdAtTimestamp: { _lte: $before, _gte: $after }\n          leadId: { _eq: $host }\n        }\n        blockchain: ALL\n        limit: $limit\n      }\n    ) {\n      FarcasterChannel {\n        name\n        description\n        imageUrl\n        createdAtTimestamp\n        warpcastUrl: channelId\n      }\n    }\n  }\n";
