export declare const farcasterUserReplies = "\n  query FarcasterUserReplies($identity: Identity!, $limit: Int! = 200) {\n    FarcasterReplies(\n      input: {\n        filter: { repliedBy: { _eq: $identity } }\n        blockchain: ALL\n        limit: $limit\n      }\n    ) {\n      Reply {\n        hash\n        castedAtTimestamp\n        embeds\n        url\n        text\n        numberOfRecasts\n        numberOfLikes\n        numberOfReplies\n        channel {\n          channelId\n        }\n        mentions {\n          fid\n          position\n        }\n        frame {\n          frameHash\n          frameUrl\n        }\n      }\n    }\n  }\n";
